import React, { useState } from 'react';
import Comptes from './Comptes';
import Transactions from './Transactions';

function App() {
  const [showComptes, setShowComptes] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  // Display accounts
  const handleComptesClick = () => {
    setShowComptes(true);
    setShowTransactions(false);
  };

  // Display transactions
  const handleTransactionsClick = () => {
    setShowTransactions(true);
    setShowComptes(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-gradient-start to-secondary-gradient-end text-white relative">
      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center py-20 space-y-10">
        {/* Animated Title */}
        <h1 className="text-6xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-teal-400 to-blue-500 animate-pulse">
          Manage Your Finances Seamlessly
        </h1>
        <p className="text-gray-300 max-w-xl text-lg">
          Experience the modern way to handle your accounts and transactions with ease!
        </p>

        {/* Floating Buttons */}
        <div className="flex justify-center space-x-8 mt-12">
          <button
            onClick={handleComptesClick}
            className="px-8 py-3 rounded-full text-lg font-semibold bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 shadow-lg transform hover:scale-110 animate-bounce transition"
          >
            View Accounts
          </button>
          <button
            onClick={handleTransactionsClick}
            className="px-8 py-3 rounded-full text-lg font-semibold bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 shadow-lg transform hover:scale-110 animate-bounce transition"
          >
            View Transactions
          </button>
        </div>
      </section>

      {/* Content Section */}
      <main className="container mx-auto px-6 py-10 space-y-10">
        {showComptes && (
          <div className="bg-gray-800 rounded-2xl shadow-2xl p-8 animate-fade-in">
            <h2 className="text-3xl font-bold text-indigo-400 mb-4">
              Account Details
            </h2>
            <Comptes />
          </div>
        )}
        {showTransactions && (
          <div className="bg-gray-800 rounded-2xl shadow-2xl p-8 animate-fade-in">
            <h2 className="text-3xl font-bold text-pink-400 mb-4">
              Transaction Details
            </h2>
            <Transactions />
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="absolute bottom-0 w-full bg-black bg-opacity-90 py-6">
        <div className="container mx-auto text-center text-gray-500">
          &copy; 2024 Finance Manager. Designed for simplicity and creativity.
        </div>
      </footer>
    </div>
  );
}

export default App;
